import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import Navbar from "./Navbar";

export default function FileUploader() {
  const [files, setFiles] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [shakeForm, setShakeForm] = useState(false); // State for shake effect
  const [formData, setFormData] = useState({ title: "", description: "" });
  const [previews, setPreviews] = useState([]);
  const [glow, setGlow] = useState(false);

  useEffect(() => {
    if (glow) {
      const timeout = setTimeout(() => setGlow(false), 1000);
      return () => clearTimeout(timeout);
    }
  }, [glow]);

  useEffect(() => {
    if (shakeForm) {
      const timeout = setTimeout(() => setShakeForm(false), 500);
      return () => clearTimeout(timeout);
    }
  }, [shakeForm]);

  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    if (!selectedFiles.length) return;

    setFiles(selectedFiles);

    // Generate preview URLs for images
    const filePreviews = selectedFiles.map((file) => URL.createObjectURL(file));
    setPreviews(filePreviews);
  };

  const handleUpload = async () => {
    if (!files.length) {
      setGlow(true);
      return;
    }

    for (const file of files) {
      const formData = new FormData();
      formData.append("image", file);

      try {
        await axios.post("http://localhost:5000/api/img", formData, {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${
              typeof window !== "undefined" && localStorage.getItem("token")
            }`,
          },
        });
      } catch (error) {
        console.error("Upload failed:", error);
      }
    }

    if (!showForm) {
      setShowForm(true);
    } else {
      setShakeForm(true); // Trigger shake effect if form is already visible
    }
  };

  const handleFormChange = (event) => {
    const { name, value } = event.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    console.log("Form Submitted", formData);
  };

  return (
    <div>
      <Navbar />
      <style>
        {`
          .glow {
            box-shadow: 0 0 15px #00f !important;
            transition: box-shadow 0.5s ease-in-out;
          }

          .shake {
            animation: shake 0.5s ease-in-out;
            background-color: rgba(255, 0, 0, 0.85) !important; /* Light red background */
          }

          @keyframes shake {
            0% { transform: translateX(0); }
            20% { transform: translateX(-5px); }
            40% { transform: translateX(5px); }
            60% { transform: translateX(-5px); }
            80% { transform: translateX(5px); }
            100% { transform: translateX(0); }
          }
        `}
      </style>
      <div className="container mt-5">
        <div className="card p-4 shadow-lg">
          <h2 className="text-center mb-4">Upload Images & Videos</h2>
          <input
            type="file"
            className={`form-control mb-3 ${glow ? "glow" : ""}`}
            accept="image/*, video/*"
            multiple
            onChange={handleFileChange}
          />
          <div className="preview-container d-flex flex-wrap">
            {previews.map((src, index) => (
              <img
                key={index}
                src={src}
                alt="Preview"
                className="m-2"
                style={{ width: 100, height: 100, objectFit: "cover" }}
              />
            ))}
          </div>
          <button className="btn btn-primary mt-3" onClick={handleUpload}>
            Upload Files
          </button>
        </div>

        {showForm && (
          <div className={`card p-4 shadow-lg mt-4 ${shakeForm ? "shake" : ""}`}>
            <h3 className="text-center">Enter Details</h3>
            <form onSubmit={handleFormSubmit}>
              <div className="mb-3">
                <label className="form-label">Title</label>
                <input
                  type="text"
                  className="form-control"
                  name="title"
                  value={formData.title}
                  onChange={handleFormChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Description</label>
                <textarea
                  className="form-control"
                  name="description"
                  value={formData.description}
                  onChange={handleFormChange}
                  required
                />
              </div>
              <button type="submit" className="btn btn-success">
                Submit
              </button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
}
