import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios"; // Import axios  
import Navbar from './Navbar'

export default function FileUploader() {
  const [files, setFiles] = useState([]);

  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    if (!selectedFiles.length) return;
    setFiles(selectedFiles);
  };

  const handleUpload = async () => {
    if (!files.length) return;

    const uploadedFiles = []; // Store uploaded file info

    for (const file of files) {
      const formData = new FormData();
      formData.append("image", file);

      try {
        const response = await axios.post(
          "http://localhost:5000/api/img",
          formData,
          {
            headers: {
              "Content-Type": "multipart/form-data",
              Authorization: `Bearer ${
                typeof window !== "undefined" && localStorage.getItem("token")
              }`, // Check if localStorage is available
            },
          }
        );

        uploadedFiles.push({
          id: Date.now(),
          type: file.type.startsWith("video/") ? "video" : "image",
          url: response.data.imageUrl,
        });
      } catch (error) {
        if (error.response) {
          console.error("Upload failed:", error.response.data.message);
        } else if (error.request) {
          console.error("Network error:", error.request);
        } else {
          console.error("Error uploading file:", error.message);
        }
      }
    }
    setFiles([]); // Clear selected files after upload
    document.querySelector("input[type=file]").value = ""; // Reset file input
  };

  return (
    <div>
        <Navbar/>

    <div className="container mt-5">
      <div className="card p-4 shadow-lg">
        <h2 className="text-center mb-4">Upload Images & Videos</h2>
        <input
          type="file"
          className="form-control mb-3"
          accept="image/*, video/*"
          multiple
          onChange={handleFileChange}
        />
        <button className="btn btn-primary" onClick={handleUpload}>
          Upload Files
        </button>
        <div className="row mt-3">
          {files.map((file, index) => (
            <div key={index} className="col-md-4 mb-3">
              {file.type.startsWith("image") ? (
                <img
                  src={URL.createObjectURL(file)}
                  className="img-fluid rounded shadow-sm"
                  alt="uploaded"
                />
              ) : (
                <video controls className="img-fluid rounded shadow-sm">
                  <source src={URL.createObjectURL(file)} type={file.type} />
                </video>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
    </div>
  );
}
