// UserPhotos.js
import React, { useState, useEffect } from "react";
import axios from "axios";

export default function UserPhotos() {
  const [imgurl, setImgurl] = useState(null); // Initialize with null

  useEffect(() => {
    const fetchImgurl = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get(
          "http://localhost:5000/api/ph/photos",
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setImgurl(response.data.imgurl); // Store the single URL
      } catch (error) {
        console.error("Error fetching imgurl:", error);
      }
    };
    fetchImgurl();
  }, []);

  return (
    <div>
      <h2>Your Photo</h2>
      {imgurl ? ( // Check if imgurl exists
        <img src={imgurl} alt="User Photo" style={{ maxWidth: "500px" }} />
      ) : (
        <p>No photo found.</p>
      )}
    </div>
  );
}
