import { useForm } from "react-hook-form";
import { Link } from "react-router-dom";
import React, { useState } from "react";  // Import useState
//import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";  // Import useNavigate
import { loginUser } from "../api/auth";
import "bootstrap/dist/css/bootstrap.min.css";

const Login = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();
  const navigate = useNavigate(); // Correctly define useNavigate
  const [errorMessage, setErrorMessage] = useState("");

  const onSubmit = async (data) => {
    try {
      const result = await loginUser({contact: data.contact,password: data.password}); // Use correct key "username"

      if (result.token) {
        localStorage.setItem("token", result.token); // Store token
        alert("Login Successful!");
        navigate("/dashboard"); // Redirect to dashboard
      } else {
        throw new Error(result.message || "Invalid credentials");
      }
    } catch (error) {
      console.error("Login Error:", error);
      setErrorMessage(error.message);
    }
  };  return (
    <div className="d-flex justify-content-center align-items-center vh-100 bg-dark">
      <div className="card p-4 shadow-lg bg-secondary text-white" style={{ width: "350px" }}>
        <h5 className="text-center mb-3">Sign in to GitHub</h5>
        <form onSubmit={handleSubmit(onSubmit)}>
          {/* Username/Email Field */}
          <div className="mb-3">
            <label className="form-label">Username or email address</label>
            <input
              type="text"
              className={`form-control bg-dark text-white ${errors.username ? "is-invalid" : ""}`}
              placeholder=""
              {...register("contact", { required: "Username is required" })}
            />
            {errors.username && <div className="invalid-feedback">{errors.username.message}</div>}
          </div>

          {/* Password Field */}
          <div className="mb-2 d-flex justify-content-between">
            <label className="form-label">Password</label>
            <Link to="/forgot-password" className="text-info small">Forgot password?</Link>
          </div>
          <div className="mb-3">
            <input
              type="password"
              className={`form-control bg-dark text-white ${errors.password ? "is-invalid" : ""}`}
              placeholder=""
              {...register("password", {
                required: "Password is required",
                minLength: { value: 6, message: "Password must be at least 6 characters" },
              })}
            />
            {errors.password && <div className="invalid-feedback">{errors.password.message}</div>}
          </div>

          {/* Login Button */}
          <button type="submit" className="btn btn-success w-100">
            Sign in
          </button>
        </form>

        {/* Additional Links */}
        <div className="mt-3 text-center">
          <Link to="/passkey-login" className="text-info d-block">Sign in with a passkey</Link>
        </div>
        <div className="mt-3 p-3 bg-dark rounded text-center">
          <span className="text-light">New to GitHub? </span>
          <Link to="/signup" className="text-info">Create an account</Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
