import React, { useState } from 'react';
import Navbar from './Navbar';
import { Button, TextField, List, ListItem, ListItemText, IconButton, Paper } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

function Profile() {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState("");

  const addTask = () => {
    if (task.trim()) {
      setTasks([...tasks, task]);
      setTask("");
    }
  };

  const removeTask = (index) => {
    setTasks(tasks.filter((_, i) => i !== index));
  };

  // Handle Enter Key Press to Add Task
  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      addTask();
    }
  };

  return (
    <div style={{ fontFamily: "'Poppins', sans-serif" }}>
      {/* Google Font Link */}
      <link
        href="https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600&display=swap"
        rel="stylesheet"
      />

      <Navbar />
      <div className="container mt-5">
        <h2 className="text-center" style={{ fontSize: "2.5rem", fontWeight: "600", display: "flex", alignItems: "center", justifyContent: "center", gap: "15px" }}>
          Add New
          <span className="sand-clock"></span>
        </h2>
        <div className="d-flex flex-column align-items-center mb-3" style={{ marginTop: "20px" }}>
          <TextField
            label="New Task"
            variant="outlined"
            value={task}
            onChange={(e) => setTask(e.target.value)}
            onKeyPress={handleKeyPress}  // Listening for Enter key
            className="me-2"
            style={{ fontSize: "1.5rem", width: "50%" }}
          />
          <Button variant="contained" color="primary" onClick={addTask} style={{ fontSize: "1.2rem", marginTop: "10px" }}>
            Add
          </Button>
        </div>
        <List>
          {tasks.map((t, index) => (
            <Paper
              key={index}
              elevation={3}
              style={{
                marginBottom: "15px",
                padding: "15px",
                borderRadius: "10px",
                background: "#f5deb3",
                fontFamily: "'Poppins', sans-serif",
                fontSize: "1.5rem",
                fontWeight: "500",
                boxShadow: "5px 5px 15px rgba(0, 0, 0, 0.2)",
                border: "2px solid #c4a484",
                transform: "rotate(-2deg)",
                transition: "0.3s ease-in-out",
                position: "relative",
                overflow: "hidden"
              }}
              className="task-item"
            >
              <div className="scroll-texture"></div>
              <ListItem
                secondaryAction={
                  <IconButton
                    edge="end"
                    onClick={() => removeTask(index)}
                    className="delete-btn"
                  >
                    <DeleteIcon color="error" />
                  </IconButton>
                }
              >
                <ListItemText primary={t} />
              </ListItem>
            </Paper>
          ))}
        </List>
      </div>

      {/* Hover & Click Animation Styling */}
      <style>
        {`
          .task-item:hover {
            box-shadow: 0 0 20px #c4a484;
            transform: rotate(0deg) scale(1.05);
          }

          .delete-btn {
            transition: transform 0.2s ease-in-out, color 0.2s ease-in-out;
          }

          .delete-btn:hover {
            transform: scale(1.3);
            color: red;
          }

          .delete-btn:active {
            transform: scale(0.9);
          }

          .scroll-texture {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: url('https://www.transparenttextures.com/patterns/aged-paper.png');
            opacity: 0.5;
          }

          .sand-clock {
            width: 30px;
            height: 40px;
            background: url('https://i.imgur.com/Ot7E1rF.png') no-repeat center center;
            background-size: contain;
            animation: sandflow 4s infinite linear;
          }

          @keyframes sandflow {
            0% { transform: rotate(0deg); }
            50% { transform: rotate(180deg); }
            100% { transform: rotate(360deg); }
          }
        `}
      </style>
    </div>
  );
}

export default Profile;
