import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Navbar from "./Navbar";
import useAuthCheck from "../api/auth-check";

export default function Profile() {
  useAuthCheck(); // Runs authentication check

  const [files, setFiles] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({ title: "", description: "", date: "" });
  const [previews, setPreviews] = useState([]);
  const [shake, setShake] = useState(false);
  const navigate = useNavigate();

  // File selection handler
  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    if (!selectedFiles.length) return;

    setFiles(selectedFiles);
    const filePreviews = selectedFiles.map(file => URL.createObjectURL(file));
    setPreviews(filePreviews);
  };

  // Upload handler
  const handleUpload = async () => {
    if (!files.length) {
      setShake(true);
      setTimeout(() => setShake(false), 500);
      return;
    }

    for (const file of files) {
      const uploadData = new FormData();
      uploadData.append("image", file);

      try {
        await axios.post("http://localhost:5000/api/img", uploadData, {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });
      } catch (error) {
        console.error("Upload failed:", error);
      }
    }
    setShowForm(true);
  };

  // Form change handler
  const handleFormChange = (event) => {
    const { name, value } = event.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Form submission handler
  const handleFormSubmit = async (event) => {
    event.preventDefault();

    // Check if form fields are filled
    if (!formData.title || !formData.description || !formData.date) {
      alert("Please fill in all fields.");
      return;
    }

    console.log("Form Submitted:", formData);

    try {
      await axios.post("http://localhost:5000/api/details", formData, {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });

      alert("Form submitted successfully!");
      setFormData({ title: "", description: "", date: "" });
      setShowForm(false);
    } catch (error) {
      console.error("Form submission failed:", error);
      alert("Submission failed! Try again.");
    }
  };

  return (
    <div>
      <Navbar />
      <style>
        {`
          .shake {
            animation: shake 0.5s ease-in-out;
            background-color: rgba(255, 0, 0, 0.1);
          }
          @keyframes shake {
            0% { transform: translateX(0); }
            25% { transform: translateX(-5px); }
            50% { transform: translateX(5px); }
            75% { transform: translateX(-5px); }
            100% { transform: translateX(0); }
          }
        `}
      </style>

      <div className="container mt-5">
        <div className={`card p-4 shadow-lg ${shake ? "shake" : ""}`}>
          <h2 className="text-center mb-4">Upload Images & Videos</h2>
          <input
            type="file"
            className="form-control mb-3"
            accept="image/*, video/*"
            multiple
            onChange={handleFileChange}
          />
          <div className="preview-container d-flex flex-wrap">
            {previews.map((src, index) => (
              <img key={index} src={src} alt="Preview" className="m-2" style={{ width: 100, height: 100, objectFit: "cover" }} />
            ))}
          </div>
          <button className="btn btn-primary mt-3 me-2" onClick={handleUpload}>
            Upload Files
          </button>
          {showForm && (
            <button className="btn btn-secondary mt-3" onClick={() => setShowForm(true)}>
              Add More
            </button>
          )}
        </div>

        {showForm && (
          <div className="card p-4 shadow-lg mt-4">
            <h3 className="text-center">Enter Details</h3>
            <form onSubmit={handleFormSubmit}>
              <div className="mb-3">
                <label className="form-label">Title</label>
                <input
                  type="text"
                  className="form-control"
                  name="title"
                  value={formData.title}
                  onChange={handleFormChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Description</label>
                <textarea
                  className="form-control"
                  name="description"
                  value={formData.description}
                  onChange={handleFormChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Select Time (YYYY-MM-DD)</label>
                <input
                  type="date"
                  className="form-control"
                  name="date"
                  value={formData.date}
                  onChange={handleFormChange}
                  required
                  min={new Date().toISOString().split("T")[0]} // Only future dates allowed
                />
              </div>
              <button type="submit" className="btn btn-success">
                Submit
              </button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
}
