import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import Navbar from "./Navbar";

export default function Profile() {
  const [files, setFiles] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [shakeForm, setShakeForm] = useState(false);
  const [formData, setFormData] = useState({ title: "", description: "", time: "" });
  const [previews, setPreviews] = useState([]);
  const [glow, setGlow] = useState(false);
  const [showAddMore, setShowAddMore] = useState(false);

  useEffect(() => {
    if (glow) {
      const timeout = setTimeout(() => setGlow(false), 1000);
      return () => clearTimeout(timeout);
    }
  }, [glow]);

  useEffect(() => {
    if (shakeForm) {
      const timeout = setTimeout(() => setShakeForm(false), 500);
      return () => clearTimeout(timeout);
    }
  }, [shakeForm]);

  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    if (!selectedFiles.length) return;

    setFiles((prevFiles) => [...prevFiles, ...selectedFiles]);

    // Generate preview URLs for images
    const filePreviews = selectedFiles.map((file) => URL.createObjectURL(file));
    setPreviews((prevPreviews) => [...prevPreviews, ...filePreviews]);
  };

  const handleUpload = async () => {
    if (!files.length) {
      setGlow(true);
      return;
    }

    for (const file of files) {
      const formData = new FormData();
      formData.append("image", file);

      try {
        await axios.post("http://localhost:5000/api/img", formData, {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${
              typeof window !== "undefined" && localStorage.getItem("token")
            }`,
          },
        });
      } catch (error) {
        console.error("Upload failed:", error);
      }
    }

    if (!showForm) {
      setShowForm(true);
      setShowAddMore(true);
    } else {
      setShakeForm(true);
    }
  };

  const handleFormChange = (event) => {
    const { name, value } = event.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault(); // Prevent page reload

    if (!formData.title || !formData.description || !formData.time) {
      alert("Please fill in all fields.");
      return;
    }

    console.log("Submitting form:", formData);

    try {
      const response = await axios.post("http://localhost:5000/api/details", formData, {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${
            typeof window !== "undefined" && localStorage.getItem("token")
          }`,
        },
      });

      console.log("Form submission success:", response.data);
      alert("Form submitted successfully!");

      // Reset form
      setFormData({ title: "", description: "", time: "" });
      setShowForm(false);
    } catch (error) {
      if (error.response) {
        console.error("Form submission failed:", error.response.data.message);
      } else if (error.request) {
        console.error("Network error:", error.request);
      } else {
        console.error("Error submitting form:", error.message);
      }
      alert("Submission failed! Try again.");
    }
  };

  return (
    <div>
      <Navbar />
      <style>
        {`
          .glow {
            box-shadow: 0 0 15px #00f !important;
            transition: box-shadow 0.5s ease-in-out;
          }

          .shake {
            animation: shake 0.5s ease-in-out;
            background-color: rgba(255, 0, 0, 0.2) !important; /* Light red background */
          }

          @keyframes shake {
            0% { transform: translateX(0); }
            20% { transform: translateX(-5px); }
            40% { transform: translateX(5px); }
            60% { transform: translateX(-5px); }
            80% { transform: translateX(5px); }
            100% { transform: translateX(0); }
          }
        `}
      </style>
      <div className="container mt-5">
        <div className="card p-4 shadow-lg">
          <h2 className="text-center mb-4">Upload Images & Videos</h2>
          <input
            type="file"
            className={`form-control mb-3 ${glow ? "glow" : ""}`}
            accept="image/*, video/*"
            multiple
            onChange={handleFileChange}
          />
          <div className="preview-container d-flex flex-wrap">
            {previews.map((src, index) => (
              <img
                key={index}
                src={src}
                alt="Preview"
                className="m-2"
                style={{ width: 100, height: 100, objectFit: "cover" }}
              />
            ))}
          </div>
          <div className="d-flex mt-3">
            <button className="btn btn-primary me-2" onClick={handleUpload}>
              Upload Files
            </button>
            {showAddMore && (
              <button className="btn btn-secondary" onClick={() => document.getElementById("fileInput").click()}>
                Add More
              </button>
            )}
          </div>
        </div>

        {showForm && (
          <div className={`card p-4 shadow-lg mt-4 ${shakeForm ? "shake" : ""}`}>
            <h3 className="text-center">Enter Details</h3>
            <form onSubmit={handleFormSubmit}>
              <div className="mb-3">
                <label className="form-label">Title</label>
                <input
                  type="text"
                  className="form-control"
                  name="title"
                  value={formData.title}
                  onChange={handleFormChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Description</label>
                <textarea
                  className="form-control"
                  name="description"
                  value={formData.description}
                  onChange={handleFormChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Time</label>
                <input
                  type="time"
                  className="form-control"
                  name="time"
                  value={formData.time}
                  onChange={handleFormChange}
                  required
                />
              </div>
              <button type="submit" className="btn btn-success">
                Submit
              </button>
            </form>
          </div>
        )}

        <input
          type="file"
          id="fileInput"
          className="d-none"
          accept="image/*, video/*"
          multiple
          onChange={handleFileChange}
        />
      </div>
    </div>
  );
}
