// CountdownTimer.js
import { useState, useEffect } from "react";
import "./CountdownTimer.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function CountdownTimer({ initialTime, isDarkMode }) { // Accept initialTime prop
    const [time, setTime] = useState(initialTime); // Use initialTime
    const [isRunning, setIsRunning] = useState(true); // Start automatically
    const navigate = useNavigate();

    useEffect(() => {
        let interval;
        if (isRunning && time > 0) {
            interval = setInterval(() => {
                setTime((prevTime) => prevTime - 1);
            }, 1000);
        } else {
            clearInterval(interval);
            if (isRunning && time === 0) {
                // ... (send email logic) ...
                setIsRunning(false);
                navigate("/user-photos");
            }
        }
        return () => clearInterval(interval);
    }, [isRunning, time, navigate]);

    // ... (formatTime function) ...

    return (
        <div className={isDarkMode ? "dark-mode" : "light-mode"}>
            <div className="timer">{formatTime(time)}</div>
            <div className="button-container">
                <button onClick={() => setIsRunning(false)} className="button">
                    Stop
                </button>
                <button
                    onClick={() => {
                        setTime(initialTime); // Reset to initialTime
                        setIsRunning(true);
                    }}
                    className="button"
                >
                    Reset
                </button>
            </div>
        </div>
    );
}
