import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import Navbar from './Navbar';

export default function FileUploader() {
  const [files, setFiles] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({ title: "", description: "" });

  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    if (!selectedFiles.length) return;
    setFiles(selectedFiles);
  };

  const handleUpload = async () => {
    if (!files.length) return;

    for (const file of files) {
      const formData = new FormData();
      formData.append("image", file);

      try {
        await axios.post("http://localhost:5000/api/img", formData, {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${
              typeof window !== "undefined" && localStorage.getItem("token")
            }`,
          },
        });
      } catch (error) {
        console.error("Upload failed:", error);
      }
    }
    setFiles([]);
    document.querySelector("input[type=file]").value = "";
    setShowForm(true); // Show the form after upload
  };

  const handleFormChange = (event) => {
    const { name, value } = event.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    console.log("Form Submitted", formData);
  };

  return (
    <div>
      <Navbar />
      <div className="container mt-5">
        <div className="card p-4 shadow-lg">
          <h2 className="text-center mb-4">Upload Images & Videos</h2>
          <input
            type="file"
            className="form-control mb-3"
            accept="image/*, video/*"
            multiple
            onChange={handleFileChange}
          />
          <button className="btn btn-primary" onClick={handleUpload}>
            Upload Files
          </button>
        </div>

        {/* Show form after upload */}
        {showForm && (
          <div className="card p-4 shadow-lg mt-4">
            <h3 className="text-center">Enter Details</h3>
            <form onSubmit={handleFormSubmit}>
              <div className="mb-3">
                <label className="form-label">Title</label>
                <input
                  type="text"
                  className="form-control"
                  name="title"
                  value={formData.title}
                  onChange={handleFormChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Description</label>
                <textarea
                  className="form-control"
                  name="description"
                  value={formData.description}
                  onChange={handleFormChange}
                  required
                />
              </div>
              <button type="submit" className="btn btn-success">
                Submit
              </button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
}
