// auth-check.js
import { isTokenExpired } from './token-utils';
import { useNavigate } from 'react-router-dom';

export function setupAuthCheck(interval = 60000) { // Default: 1 minute
    const navigate = useNavigate();

    setInterval(() => {
        if (isTokenExpired()) {
            localStorage.removeItem('token');
            navigate('/login');
        }
    }, interval);
}
