import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function FileUploader() {
    const [files, setFiles] = useState([]);
    const [time, setTime] = useState(0);
    const [profileInput, setProfileInput] = useState("");  // State to store profile input
    const navigate = useNavigate();

    useEffect(() => {
        const storedInput = localStorage.getItem("profileInput");
        if (storedInput) {
            setProfileInput(storedInput); // Retrieve the input on mount
        }
    }, []);

    const handleFileChange = (event) => {
        const selectedFiles = Array.from(event.target.files);
        if (!selectedFiles.length) return;
        setFiles(selectedFiles);
    };

    const handleTimeChange = (event) => {
        setTime(parseInt(event.target.value, 10) || 0);
    };

    const handleUpload = async (event) => {
        event.preventDefault();
        if (!files.length) return;

        for (const file of files) {
            const formData = new FormData();
            formData.append("image", file);
            formData.append("time", time);
            formData.append("profileInput", profileInput); // Send profile input to the backend

            try {
                await axios.post(
                    "http://localhost:5000/api/img",
                    formData,
                    {
                        headers: {
                            "Content-Type": "multipart/form-data",
                            Authorization: `Bearer ${
                                typeof window !== "undefined" && localStorage.getItem("token")
                            }`,
                        },
                    }
                );

                console.log("Upload successful");
                navigate(`/dashboard?time=${time}`);
            } catch (error) {
                console.error("Upload failed:", error);
            }
        }

        setFiles([]);
        document.querySelector("input[type=file]").value = "";
        setTime(0);
    };

    return (
        <div className="container mt-5">
            <div className="card p-4 shadow-lg">
                <h2 className="text-center mb-4">Upload Images & Videos</h2>

                {/* Display stored input at the top */}
                {profileInput && (
                    <div className="alert alert-info text-center">
                        Profile Input: <strong>{profileInput}</strong>
                    </div>
                )}

                <input
                    type="file"
                    className="form-control mb-3"
                    accept="image/*, video/*"
                    multiple
                    onChange={handleFileChange}
                />
                <input
                    type="number"
                    className="form-control mb-3"
                    placeholder="Time (seconds)"
                    value={time}
                    onChange={handleTimeChange}
                />
                <button className="btn btn-primary" onClick={handleUpload}>
                    Upload & Go to Dashboard
                </button>
            </div>
        </div>
    );
}
