import React, { useContext } from "react";
import { useForm } from "react-hook-form";
import { Link } from "react-router-dom";
import { ThemeContext } from "../context/ThemeContext"; // Import Dark Mode Context
import "./css/images"; // Import Login CSS

const Login = () => {
  const { darkMode } = useContext(ThemeContext); // Get dark mode state
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => {
    console.log("Login Data:", data);
    alert("Login Successful!");
  };

  return (
    <div className={`login-container ${darkMode ? "dark-mode" : ""}`}>
      {/* Overlay */}
      <div className="overlay"></div>

      {/* Login Form */}
      <div className="login-box">
        <h2 className="text-center">Login</h2>
        <form onSubmit={handleSubmit(onSubmit)}>
          {/* Username Field */}
          <div className="mb-3">
            <label className="form-label">Username</label>
            <input
              type="text"
              className={`form-control ${errors.username ? "is-invalid" : ""}`}
              placeholder="Enter your username"
              {...register("username", { required: "Username is required" })}
            />
            {errors.username && <div className="invalid-feedback">{errors.username.message}</div>}
          </div>

          {/* Password Field */}
          <div className="mb-3">
            <label className="form-label">Password</label>
            <input
              type="password"
              className={`form-control ${errors.password ? "is-invalid" : ""}`}
              placeholder="Enter password"
              {...register("password", {
                required: "Password is required",
                minLength: {
                  value: 6,
                  message: "Password must be at least 6 characters",
                },
              })}
            />
            {errors.password && <div className="invalid-feedback">{errors.password.message}</div>}
          </div>

          {/* Login Button */}
          <button type="submit" className="btn btn-primary w-100">Login</button>
        </form>

        <p className="text-center mt-3">
          Don't have an account?{" "}
          <Link className="nav-link d-inline" to="/signup">Signup</Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
