import React, { useState } from 'react';
import Navbar from './Navbar';
import { Button, TextField, List, ListItem, ListItemText, IconButton, Paper } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

function Profile() {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState("");

  const addTask = () => {
    if (task.trim()) {
      setTasks([...tasks, task]);
      setTask("");
    }
  };

  const removeTask = (index) => {
    setTasks(tasks.filter((_, i) => i !== index));
  };

  return (
    <div>
      <Navbar />
      <div className="container mt-5">
        <h2 className="text-center">To-Do List</h2>
        <div className="d-flex justify-content-center mb-3">
          <TextField
            label="New Task"
            variant="outlined"
            value={task}
            onChange={(e) => setTask(e.target.value)}
            className="me-2"
          />
          <Button variant="contained" color="primary" onClick={addTask}>
            Add
          </Button>
        </div>
        <List>
          {tasks.map((t, index) => (
            <Paper key={index} elevation={3} style={{ marginBottom: "10px", padding: "10px", borderRadius: "8px", border: "2px solid #007bff" }}>
              <ListItem
                secondaryAction={
                  <IconButton edge="end" onClick={() => removeTask(index)}>
                    <DeleteIcon color="error" />
                  </IconButton>
                }
              >
                <ListItemText primary={t} />
              </ListItem>
            </Paper>
          ))}
        </List>
      </div>
    </div>
  );
}

export default Profile;
