import React, { useState, useEffect } from "react";
import axios from "axios";

export default function UserPhotos() {
    const [imgurl, setImgurl] = useState([]);

    useEffect(() => {
        const fetchImgurl = async () => {
            try {
                const token = localStorage.getItem("token");
                const response = await axios.get("http://localhost:5000/api/ph/photos", {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                setImgurl(response.data.imgurl);
            } catch (error) {
                console.error("Error fetching imgurl:", error);
            }
        };
        fetchImgurl();
    }, []);

    const isVideo = (url) => {
        const videoExtensions = ['.mp4', '.webm', '.ogg']; // Add more if needed
        const lowerCaseUrl = url.toLowerCase();
        return videoExtensions.some(ext => lowerCaseUrl.endsWith(ext));
    };

    return (
        <div>
            <h2>Your Photos and Videos</h2>
            {imgurl.length > 0 ? (
                <div style={{ display: "flex", flexWrap: "wrap" }}>
                    {imgurl.map((url, index) => (
                        <div key={index} style={{ maxWidth: "300px", margin: "10px" }}>
                            {isVideo(url) ? (
                                <video controls width="100%">
                                    <source src={url} type="video/mp4" /> {/* Adjust type if needed */}
                                    Your browser does not support the video tag.
                                </video>
                            ) : (
                                <img src={url} alt={`User Media ${index}`} width="100%" />
                            )}
                        </div>
                    ))}
                </div>
            ) : (
                <p>No media found.</p>
            )}
        </div>
    );
}
