import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import { Button, TextField, List, ListItem, ListItemText, IconButton, Paper } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

function Profile() {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState("");
  const [darkMode, setDarkMode] = useState(localStorage.getItem("darkMode") === "true");
  const navigate = useNavigate();

  useEffect(() => {
    // Apply dark mode class to body on mount
    if (darkMode) {
      document.body.classList.add("dark-mode");
    } else {
      document.body.classList.remove("dark-mode");
    }
  }, [darkMode]);

  const toggleDarkMode = () => {
    const newDarkMode = !darkMode;
    setDarkMode(newDarkMode);
    localStorage.setItem("darkMode", newDarkMode);

    if (newDarkMode) {
      document.body.classList.add("dark-mode");
    } else {
      document.body.classList.remove("dark-mode");
    }
  };

  const addTask = () => {
    if (task.trim()) {
      setTasks([...tasks, task]);
      setTask("");
      navigate('/upload');
    }
  };

  const removeTask = (index) => {
    setTasks(tasks.filter((_, i) => i !== index));
  };

  return (
    <div style={{ fontFamily: "'Poppins', sans-serif" }}>
      <Navbar onToggleDarkMode={toggleDarkMode} darkMode={darkMode} />

      <div className="container mt-5">
        <h2 className="text-center sand-clock-container">
          Add New
          <span className="sand-clock">âŒ›</span>
        </h2>

        <div className="d-flex justify-content-center mb-3">
          <TextField
            label="New Task"
            variant="outlined"
            value={task}
            onChange={(e) => setTask(e.target.value)}
            onKeyPress={(e) => e.key === "Enter" && addTask()}
            className="me-2"
            style={{ fontSize: "1.5rem" }}
          />
          <Button variant="contained" color="primary" onClick={addTask} style={{ fontSize: "1.2rem" }}>
            Add
          </Button>
        </div>

        <List>
          {tasks.map((t, index) => (
            <Paper key={index} elevation={3} className="locker-task">
              <ListItem
                secondaryAction={
                  <IconButton edge="end" onClick={() => removeTask(index)} className="delete-btn">
                    <DeleteIcon color="error" />
                  </IconButton>
                }
              >
                <ListItemText primary={t} primaryTypographyProps={{ style: { fontSize: "1.5rem", fontWeight: "500" } }} />
              </ListItem>
            </Paper>
          ))}
        </List>
      </div>
    </div>
  );
}

export default Profile;
