import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import Navbar from "./Navbar";

export default function Profile() {
  const [files, setFiles] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [shakeForm, setShakeForm] = useState(false);
  const [previews, setPreviews] = useState([]);
  const [glow, setGlow] = useState(false);
  const [showAddMore, setShowAddMore] = useState(false);

  useEffect(() => {
    if (glow) {
      setTimeout(() => setGlow(false), 1000);
    }
  }, [glow]);

  useEffect(() => {
    if (shakeForm) {
      setTimeout(() => setShakeForm(false), 500);
    }
  }, [shakeForm]);

  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    if (!selectedFiles.length) return;

    setFiles((prevFiles) => [...prevFiles, ...selectedFiles]);
    setPreviews((prevPreviews) => [...prevPreviews, ...selectedFiles.map(file => URL.createObjectURL(file))]);
  };

  const handleUpload = async () => {
    if (!files.length) {
      setGlow(true);
      return;
    }

    for (const file of files) {
      const uploadData = new FormData();
      uploadData.append("image", file);

      try {
        const response = await axios.post("http://localhost:5000/api/img", uploadData, {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });

        console.log("Upload successful:", response.data);
      } catch (error) {
        console.error("Upload failed:", error);
      }
    }

    if (!showForm) {
      setShowForm(true);
      setShowAddMore(true);
    } else {
      setShakeForm(true);
    }
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault(); // Prevent page reload

    if (!files.length) {
      alert("Please select at least one file.");
      return;
    }

    console.log("Uploading images:", files);

    try {
      for (const file of files) {
        const formData = new FormData();
        formData.append("image", file);

        const response = await axios.post("http://localhost:5000/api/img", formData, {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });

        console.log("Image upload success:", response.data);
      }

      alert("Images uploaded successfully!");

      // Reset files & previews
      setFiles([]);
      setPreviews([]);
      document.querySelector("input[type=file]").value = ""; // Clear file input
      setShowForm(false); // Hide form after upload
    } catch (error) {
      console.error("Image upload failed:", error);
      alert("Image upload failed! Try again.");
    }
  };

  return (
    <div>
      <Navbar />
      <style>
        {`
          .glow {
            box-shadow: 0 0 15px #00f !important;
            transition: box-shadow 0.5s ease-in-out;
          }

          .shake {
            animation: shake 0.5s ease-in-out;
            background-color: rgba(255, 0, 0, 0.2) !important; /* Light red background */
          }

          @keyframes shake {
            0% { transform: translateX(0); }
            20% { transform: translateX(-5px); }
            40% { transform: translateX(5px); }
            60% { transform: translateX(-5px); }
            80% { transform: translateX(5px); }
            100% { transform: translateX(0); }
          }
        `}
      </style>

      <div className="container mt-5">
        <div className="card p-4 shadow-lg">
          <h2 className="text-center mb-4">Upload Images & Videos</h2>
          <input
            type="file"
            className={`form-control mb-3 ${glow ? "glow" : ""}`}
            accept="image/*, video/*"
            multiple
            onChange={handleFileChange}
          />
          <div className="preview-container d-flex flex-wrap">
            {previews.map((src, index) => (
              <img key={index} src={src} alt="Preview" className="m-2" style={{ width: 100, height: 100, objectFit: "cover" }} />
            ))}
          </div>
          <div className="d-flex mt-3">
            <button className="btn btn-primary me-2" onClick={handleUpload}>
              Upload Files
            </button>
            {showAddMore && (
              <button className="btn btn-secondary" onClick={() => document.getElementById("fileInput").click()}>
                Add More
              </button>
            )}
          </div>
        </div>

        {showForm && (
          <div className={`card p-4 shadow-lg mt-4 ${shakeForm ? "shake" : ""}`}>
            <h3 className="text-center">Upload Selected Images</h3>
            <form onSubmit={handleFormSubmit}>
              <button type="submit" className="btn btn-success w-100">
                Submit Images
              </button>
            </form>
          </div>
        )}

        <input
          type="file"
          id="fileInput"
          className="d-none"
          accept="image/*, video/*"
          multiple
          onChange={handleFileChange}
        />
      </div>
    </div>
  );
}
