import React, { useState } from 'react';
import Navbar from './Navbar';
import { Button, TextField, List, ListItem, ListItemText, IconButton, Paper } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

function Profile() {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState("");

  const addTask = () => {
    if (task.trim()) {
      setTasks([...tasks, task]);
      setTask("");
    }
  };

  const removeTask = (index) => {
    setTasks(tasks.filter((_, i) => i !== index));
  };

  // Handle Enter Key Press to Add Task
  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      addTask();
    }
  };

  return (
    <div style={{ fontFamily: "'Poppins', sans-serif" }}>
      {/* Google Font Link */}
      <link
        href="https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600&display=swap"
        rel="stylesheet"
      />

      <Navbar />

      <div className="container mt-5">
        {/* Sand Clock with Text */}
        <h2 className="text-center sand-clock-container">
          Add New
          <span className="sand-clock">⌛</span>
        </h2>

        {/* Added More Space Here */}
        <div style={{ marginTop: "20px" }}></div>

        <div className="d-flex justify-content-center mb-3">
          <TextField
            label="New Capsule"
            variant="outlined"
            value={task}
            onChange={(e) => setTask(e.target.value)}
            onKeyPress={handleKeyPress}  // Listening for Enter key
            className="me-2"
            style={{ fontSize: "1.5rem" }}
          />
          <Button variant="contained" color="primary" onClick={addTask} style={{ fontSize: "1.2rem" }}>
            Add
          </Button>
        </div>

        <List>
          {tasks.map((t, index) => (
            <Paper
              key={index}
              elevation={3}
              style={{
                marginBottom: "10px",
                padding: "12px",
                borderRadius: "10px",
                border: "2px solid #007bff",
                transition: "0.3s ease-in-out",
              }}
              className="task-item"
            >
              <ListItem
                secondaryAction={
                  <IconButton
                    edge="end"
                    onClick={() => removeTask(index)}
                    className="delete-btn"
                  >
                    <DeleteIcon color="error" />
                  </IconButton>
                }
              >
                <ListItemText
                  primary={t}
                  primaryTypographyProps={{ style: { fontSize: "1.5rem", fontWeight: "500" } }}
                />
              </ListItem>
            </Paper>
          ))}
        </List>
      </div>

      {/* Hourglass Animation Styling */}
      <style>
        {`
          .sand-clock-container {
            display: flex;
            align-items: center;
            justify-content: center;
            font-size: 2.5rem;
            font-weight: 600;
            margin-bottom: 15px; /* Added spacing */
          }

          .sand-clock {
            font-size: 2.5rem;
            margin-left: 10px;
            display: inline-block;
            animation: sandFlow 3s infinite ease-in-out;
          }

          @keyframes sandFlow {
            0% {
              content: "⌛"; /* Full hourglass */
              transform: rotate(0deg);
            }
            50% {
              content: "⏳"; /* Sand flowing */
            }
            100% {
              content: "⌛"; /* Rotates back */
              transform: rotate(180deg);
            }
          }

          .task-item:hover {
            box-shadow: 0 0 20px #007bff;
            transform: scale(1.05);
          }

          .delete-btn {
            transition: transform 0.2s ease-in-out, color 0.2s ease-in-out;
          }

          .delete-btn:hover {
            transform: scale(1.3);
            color: red;
          }

          .delete-btn:active {
            transform: scale(0.9);
          }
        `}
      </style>
    </div>
  );
}

export default Profile;
